mapper.select:
select id,title,`table`,correlation_id as correlationId,url,state,create_time as createTime from oa_collaboration where state > 0 and promoter = #{userId}
and title LIKE "%"#{title}"%"
ORDER BY create_time DESC

mapper.update:
update oa_collaboration
set title = #{title}
where correlation_id = #{correlationId}

update oa_collaboration
set state = #{state},title = #{title}
where correlation_id = #{correlationId}

update oa_collaboration
set state = #{state}
where correlation_id = #{correlationId}
